<template>
  <div class="content docs active">
    <!-- HEAD -->
    <div class="head">
      <h2>Docs Control Panel</h2>
      <button @click="AddNewCat()"><i class="far fa-plus-square"></i> New Category</button>
      <button @click="browesCats()"><i class="far fa-window-restore"></i> Browes Documents</button>
    </div>

    <!-- CATEGORIES TABLE -->
    <div class="table-1">
      <table class="doc-table">
        <tr>
          <th colspan="2">Categories</th>
        </tr>
        <tr v-if="!catNames[0]">
          <td colspan="2">No Data To Show</td>
        </tr>
        <tr v-else v-for="cat in catNames" :key="cat">
          <td colspan="2">{{ cat }}</td>
        </tr>
      </table>
    </div>

    <!-- ARTICLES TABLE -->
    <div class="table-2">
      <button @click="BackToTable()" class="backToTable">
        <i class="fas fa-arrow-circle-left"></i> Back
      </button>
      <button @click="AddNewDoc()">
        <i class="far fa-plus-square"></i> New Document
      </button>
      <table class="doc-table">
        <tr>
          <th>Document</th>
          <th>Category</th>
          <th>Edit</th>
          <th>Delete</th>
        </tr>
        <tr v-if="!documents[0]">
          <td colspan="4">No Data To Show</td>
        </tr>
        <tr v-else v-for="doc in documents" :key="doc.key">
          <td>{{ doc.data.title }}</td>
          <td>{{ doc.data.category }}</td>
          <td>
            <button :id="doc.key" @click="editDoc($event)">
              <i class="far fa-edit"></i> Edit
            </button>
          </td>
          <td :id="doc.data.category">
            <button :id="doc.key" @click="deleteDoc($event)">
              <i class="far fa-trash-alt"></i> Delete
            </button>
          </td>
        </tr>
      </table>
    </div>

    <!-- CATEGORIES -->
    <div class="cat-editMode editMode">
      <button @click="BackToTable()" class="backToTable">
        <i class="fas fa-arrow-circle-left"></i> Back
      </button>
      <label>Category Name</label>
      <input type="text" placeholder="Category Name" class="cat-name" />
      <button @click="SendCatToDB($event)" class="submitCatBtn">
        <i class="far fa-edit"></i> Submit
      </button>
    </div>

    <!-- Documents -->
    <div class="doc-editMode editMode">
      <button @click="BackToTable()" class="backToTable">
        <i class="fas fa-arrow-circle-left"></i> Back
      </button>
      <label>Document Title</label>
      <input type="text" placeholder="Document Title" class="doc-title" />
      <label>Document Category</label>
      <select class="doc-category"> </select>
      <label>Document Content</label>
      <textarea class="doc-content-edit" @keyup="preview($event)"></textarea>
      <button @click="SendDocToDB($event)" class="submitDocBtn"><i class="far fa-edit"></i> Submit </button>
      <hr>
      <br>
      <div class="doc-preview"></div>
    </div>
  </div>
</template>

<style lang="scss">
.doc-content-edit{
  padding: 25px;
  background: #F8F8F8;
  font-family: "Barlow", sans-serif;
  outline: none;
  min-height: 400px;
  box-shadow: 0 3px 10px 1px rgba($color: #000000, $alpha: 0.1);
}

.doc-preview{
  padding: 60px 40px;
  width: 90%;
  @include md {
    width: 100%;
    padding: 60px 10px;
  }
  ul{
    list-style: none;
    li{
        padding: 8px 8px 8px 15px;
        background: #f9f9f9c2;
        margin-bottom: 5px;
        border-radius: 5px;
        color: #676767;
        &::before {
          content: "⇒ ";
          color: #707070;
          font-size: 120%;
          font-weight: bold;
          padding-right: 5px;
        }
    }
  }
  
  div.code-toolbar > .toolbar{
    opacity: 1!important;
    span{
      font-family: "Barlow", sans-serif;
      font-weight: bolder;
      margin-right: 8px;
      padding: 2px 10px;
      box-shadow: none;
      background: transparent;
    }
    button{
      font-family: "Barlow", sans-serif;
      padding: 5px 10px;
    }
  }

  h1, h2, h3 {
    color: #3f3d56;
    margin-top: 30px;
    margin-bottom: 15px;
    &::before {
      content: "# ";
      color: #6f4fae;
      font-size: 120%;
    }
  }
  h4, h5 {
    font-family: "Barlow", sans-serif;
    color: #3f3d56;
    margin-top: 25px;
    margin-bottom: 15px;
    &::before {
      content: "» ";
      color: #6f4fae;
    }
  }
  h6 {
    padding: 15px 20px;
    background-color: #f9fafb;
    border-left: 5px solid #eaeff4;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
    color: #999;
    font-family: "Barlow", sans-serif;
    font-weight: bold;
    &::before {
      content: "\f071";
      font-family: "Font Awesome 5 Pro";
      margin-right: 5px;
      font-weight: 700;
      color: #999;
    }
  }
  p {
    @include xxs {
      font-size: 13.5px;
    }
  }

  img{
    max-width: 80%;
    max-height: 350px;
  }

  a{
    color: #6f4fae;
    text-decoration: none;
    font-family: "Barlow", sans-serif;
  }
}
</style>

<style lang="scss" scoped>
.content {
  display: none;
  overflow-x: scroll;
  padding: 30px 20px;
  
  @include xxs {
    padding: 30px 0px;
    i{
      display: none;
    }
  }

  .head{
    padding: 25px 0;
    h2 {
      font-family: "Poppins", sans-serif;
      width: 60%;
      display: inline-block;
      padding: 0;
      margin: 0;
  
      @include xxs {
        width: 100%;
      }
    }
    button{
      width: calc(20% - 16px);
      margin-right: 8px;
      @include xxs {
        margin-top: 10px;
        width: calc(50% - 16px);
      }
    }
  }

  &.active {
    display: block;
  }
  button {
    border-radius: 10px;
    width: 100%;
    background: #384AD7;
    border: none;
    box-shadow: 1px 1px 6px rgba($color: #000, $alpha: 0.3);
    font-family: 'Poppins', sans-serif;
    &:hover {
      transform: none;
      background: rgb(47, 62, 175);
    }
  }
  table {
    width: 100%;
    .btnTh {
      padding: 0;
      border: 0;
      background: none;
    }
    tr:nth-child(even){
      background: #f6f3ff;
    }
    th {
      background: #eee;
      text-align: center;
      font-family: "Barlow", sans-serif;
      border: 0;
      padding: 15px 22px;
      font-size: 20px;
      &:first-child{
        text-align: left;
      }
    }
    td {
      text-align: center;
      border: 0;
      padding: 15px;
      font-family: 'Poppins', sans-serif;
      &:first-child{
        text-align: left;
      }
    }
  }
  .table-2 {
    display: none;
    & > button{
      width: 50%;
    }
  }
  .editMode {
    display: none;
    label {
      display: block;
      width: 100%;
      margin-top: 40px;
      font-size: 18px;
      font-family: "Barlow", sans-serif;
    }
    input,
    textarea,
    select {
      display: block;
      width: 100%;
      border-radius: 0;
      font-family: "Barlow", sans-serif;
    }
    button {
      border-radius: 0;
      width: 100%;
      background: #384AD7;
      margin: 15px 0;
      &:hover {
        transform: none;
        background: rgb(47, 62, 175);
        color: #fff;
      }
    }
    .backToTable{
      background: #eee;
      color: #262626;
      border-color: #eee;
      font-weight: bold;
      font-family: "Barlow", sans-serif;
      &:hover{
        transform: translateY(-6px);
        box-shadow: 0 5px 8px rgba($color: #000, $alpha: 0.3);
        background: #eee;
        color: #262626;
      }
    }
    .add {
      background: #eee;
      color: #262626;
      border: none;
      width: 12.5%;
      font-size: 14px;
      margin: 0;
      text-transform: uppercase;
      font-weight: bold;
      font-family: "Barlow", sans-serif;
      @include xs {
        width: 50%;
      }
      &:hover{
        transform: scale(0.95);
        background: #ccc;
        color: #000;
      }
      &:focus{
        transform: scale(0.95);
        background: #ccc;
        color: #000;
      }
    }
  }
}
</style>

<script>
export default {
  name: "HelpControl",
  data() {
    return {
      catNames: [],
      documents: []
    };
  },
  methods: {
    AddNewCat() {
      $(".table-1").hide();
      $(".head button").hide();
      $(".cat-editMode").show();
    },
    browesCats() {
      $(".table-1").hide();
      $(".head button").hide();
      $(".table-2").show();
    },
    AddNewDoc() {
      $(".table-2").hide();
      $(".doc-editMode").show();
      $(".submitDocBtn").attr("id", "new");
      $(".doc-category").attr("disabled", false);
      $(".doc-category").html("");
      this.catNames.forEach(catName => {
        var option = `<option value="${catName}">${catName}</option>`;
        $(".doc-category").append(option);
      });
    },
    BackToTable() {
      if ($(".doc-editMode").css("display") == "block") {
        $(".table-2").show();
        $(".doc-editMode").hide();
        $(".doc-title").val("");
        $(".doc-content-edit").val("");
        $(".doc-preview").html("");
        $(".submitDocBtn").attr("id", "new");
        $(".doc-content-edit").attr("id", "");
      } else if ($(".cat-editMode").css("display") == "block") {
        $(".table-1").show();
        $(".head button").show();
        $(".cat-editMode").hide();
        $(".cat-name").val("");
      } else {
        $(".table-1").show();
        $(".head button").show();
        $(".table-2").hide();
      }
    },
    SendDocToDB(e) {
      var title = $(".doc-title").val();
      var content = $(".doc-content-edit").val();
      var category = $(".doc-category").val();
      var random = Math.floor(Math.random() * 1000000000000);
      if (title != false && content != false) {
        if (e.path[0].id == "new") {
          var perority = Date.now();
          db.update(`documents/${category}/${random}`, { title, content, perority, category }).then(() => {
              Swal.fire(
                "Success",
                `Your New Document Has Been Added`,
                "success"
              );
              $(".doc-title").val("");
              $(".doc-content-edit").val("");
              var docData = {
                key: random,
                data: { title, content, perority, category }
              };
              this.documents.push(docData);
              $(".backToTable").click();
            }).catch(err => {
              Swal.fire({
                type: "error",
                title: "Error",
                text: err.message
              });
            });
        } else {
          var perority = Number($(".doc-content-edit").attr("id"));
          db.update(`documents/${category}/${e.path[0].id}`, { title, content, perority, category }).then(() => {
              Swal.fire("Success", `Document Has Been Updated`, "success");
              $(".doc-title").val("");
              $(".doc-content-edit").val("");
              this.documents.forEach(doc => {
                if (doc.key == e.path[0].id) {
                  doc.data.title = title;
                  doc.data.content = content;
                }
              });
              $(".backToTable").click();
            }).catch(err => {
              Swal.fire({
                type: "error",
                title: "Error",
                text: err.message
              });
            });
        }
      } else {
        Swal.fire({
          type: "error",
          title: "Error",
          text: "Error, Empty Fields"
        });
      }
    },
    editDoc(e) {
      var key = e.path[0].id;
      $(".table-2").hide();
      $(".doc-editMode").show();
      $(".submitDocBtn").attr("id", key);
      $(".doc-category").html("");
      this.catNames.forEach(catName => {
        var option = `<option value="${catName}">${catName}</option>`;
        $(".doc-category").append(option);
      });
      this.documents.forEach((doc, i) => {
        if (doc.key == key) {
          var title = this.documents[i].data.title;
          var content = this.documents[i].data.content;
          var category = this.documents[i].data.category;
          var perority = this.documents[i].data.perority;
          $(".doc-title").val(title);
          $(".doc-content-edit").val(content).attr("id", perority);
          $(".doc-category").val(category);
        }
      });
      $(".doc-category").attr("disabled", true);
    },
    deleteDoc(e) {
      Swal.fire({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        type: "warning",
        showCancelButton: true,
        confirmButtonText: "Yes, delete it!",
        cancelButtonText: "No, cancel!",
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33'
      }).then(result => {
        if (result.value) {
          var key = e.path[0].id;
          var category = e.path[1].id;
          db.delete(`documents/${category}/${key}`).then(() => {
              Swal.fire("Success", `Deleted Successfully`, "success").then(
                () => {
                  this.documents.forEach((doc, i) => {
                    if (doc.key == key) {
                      this.documents.splice(i, 1);
                    }
                  });
                }
              );
            }).catch(err => {
              Swal.fire({
                type: "error",
                title: "Error",
                text: err.message
              });
            });
        }
      });
    },
    SendCatToDB(e) {
      var name = $(".cat-name").val();
      if (name != false) {
        db.add(`documents/${name}`, { catName: name, perority: 0 }).then(() => {
          Swal.fire("Success", `New Category Has Been Added`, "success");
          $(".cat-name").val("");
          this.catNames.push(name);
          $(".backToTable").click();
        });
      } else {
        Swal.fire({
          type: "error",
          title: "Error",
          text: "Error, Empty Fields"
        });
      }
    },
    preview(e){
      const showdown = require("showdown");
      showdown.setFlavor('github');
      var converter = new showdown.Converter({
        tables: true,
        openLinksInNewWindow: true,
        noHeaderId: true,
        customizedHeaderId: true,
        parseImgDimensions: true,
        simpleLineBreaks: true,
        backslashEscapesHTMLTags: true,
        underline: true,
        strikethrough: true
      });
      var content = e.path[0].value;
      var html = converter.makeHtml(content);
      $(".doc-preview").html(html);
              /**********************************************PrismJS************************************************/
