  created() {
    db.get(`documents`, 'child', 'perority').then(Data => {
      if (Data) {
        Data.forEach(data => {
          this.catNames.push(data.val().catName);
          db.get(`documents/${data.val().catName}`, 'child', 'perority').then(Data2 => {
            if (Data2) {
              Data2.forEach(data2 => {
                if (typeof data2.val() == "object") {
                  var myData = {
                    key: data2.key,
                    data: data2.val()
                  };
                  this.documents.push(myData);
                }
              });
            }
          });
        });
      }
    });
    $(document).on('keydown', '.doc-content-edit', function(e) {
      var keyCode = e.keyCode || e.which;

      if (keyCode == 9) {
        e.preventDefault();
        var start = this.selectionStart;
        var end = this.selectionEnd;

        // set textarea value to: text before caret + tab + text after caret
        $(this).val($(this).val().substring(0, start) + "\t" + $(this).val().substring(end));

        // put caret at right position again
        this.selectionStart =
        this.selectionEnd = start + 1;
      } else if (keyCode == 219){
        e.preventDefault();
        var start = this.selectionStart;
        var end = this.selectionEnd;

        // set textarea value to: text before caret + tab + text after caret
        $(this).val($(this).val().substring(0, start) + "{}" + $(this).val().substring(end));

        // put caret at right position again
        this.selectionStart =
        this.selectionEnd = start + 1;
      }
    });
  }
};
</script>
